    isJourFerie(day, monthIndex = null) {
      const date = new Date(this.currentYear, monthIndex !== null ? monthIndex : this.currentMonth, typeof day === "number" ? day : new Date(day).getDate());
      const formattedDate = format(date, "yyyy-MM-dd");
      const isHoliday = this.formattedJoursFeries.some(jour => jour.date === formattedDate);
      return isHoliday;
