      const tnfDays = [];
      const resourceEvents = this.events.filter(event => event.resource === resource.name);
      for (let d = new Date(startDate); d <= endDate; d.setDate(d.getDate() + 1)) {
        // Ignorer les week-ends et les jours fériés
        if (this.isWeekend(d.getDate(), d.getMonth()).isWeekend || this.isJourFerie(d.getDate(), d.getMonth())) {
          continue;
        }
        const eventsOnDay = resourceEvents.filter(event => this.isEventOnDay(event, d));
        if (eventsOnDay.length === 0) {
          tnfDays.push(new Date(d.getFullYear(), d.getMonth(), d.getDate()));
        }
      }
      return tnfDays;
