      let selectedDate;
      if (monthIndex !== null) {
        selectedDate = new Date(this.currentYear, monthIndex, day);
      } else {
        selectedDate = new Date(this.currentYear, this.currentMonth, day);
      }
      if (!isNaN(selectedDate.getTime())) {
        const headerDate = format(selectedDate, "yyyy-MM-dd");
        this.startDate = headerDate;
        const resource = this.resources.find(r => r.name === resourceName);
        if (resource) {
          this.selectedEvent = {
            resourceName,
            resourceId: resource.id,
            day: this.startDate
          };
          this.user_id = resource.id;
          const events = monthIndex !== null ? this.getEventsForMonth(resourceName, monthIndex, day) : this.filteredEventsByDay[resourceName][day];
          const event = events && events[0];
          if (event) {
            const intervention = resource.interventions.find(i => i.id === event.id);
            if (intervention) {
              this.selectedClientId = intervention.client_id; // L'ID du client
              this.selectedClient = intervention.client_name; // Le nom du client
              this.comment = intervention.commentaire;
              this.startDate = format(new Date(intervention.date_debut), "yyyy-MM-dd");
              this.endDate = format(new Date(intervention.date_fin), "yyyy-MM-dd");
              this.selectedClientColor = intervention.client_color, this.selectedEvent.id = intervention.id; // Marquer comme événement existant
            }
          } else {
            // Réinitialiser pour un nouvel événement
            this.selectedClient = null;
            this.selectedClientId = null;
            this.comment = "";
            this.startDate = headerDate;
            this.endDate = "";
            this.selectedClientColor = ""; // Valeur par défaut
            this.selectedEvent.id = null; // Marquer comme nouvel événement
          }
          this.isEventModalOpen = true; // Ouvrir le modal
        }
      }
    },
    async deleteEvent() {
      try {
        await axios.delete(`${API_URL}/interventions/${this.selectedEvent.id}`, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`
          }
        });
        await this.fetchResourcesAndEvents();
        this.closeEventModal();
      } catch (error) {
        console.error("Erreur lors de la suppression de l'événement:", error);
        if (error.response) {
          console.error("Détails de l'erreur:", error.response.data);
        }
      }
    },
    closeEventModal() {
      this.isEventModalOpen = false;
      this.selectedEvent = {};
      this.selectedClient = null;
      this.comment = "";
      this.startDate = "";
      this.endDate = "";
      this.selectedClientColor = "";
    },
    resetToToday() {
      const today = new Date();
      this.currentYear = today.getFullYear();
      this.currentMonth = today.getMonth();
      this.currentDay = today.getDate();
    },
    prevPeriod() {
      if (this.viewMode === "month") {
        this.currentMonth--;
        if (this.currentMonth < 0) {
          this.currentMonth = 11;
          this.currentYear--;
        }
      } else {
        this.currentYear--;
      }
      this.fetchJoursFeries(); // Ajoutez cette ligne
    },
    nextPeriod() {
      if (this.viewMode === "month") {
        this.currentMonth++;
        if (this.currentMonth > 11) {
          this.currentMonth = 0;
          this.currentYear++;
        }
      } else {
        this.currentYear++;
      }
      this.fetchJoursFeries(); // Ajoutez cette ligne
    },
    getDayLabel(index, day) {
      const dayDate = new Date(this.currentYear, this.currentMonth, day);
      return format(dayDate, "EEE dd");
    },
    getDaysInMonth(monthIndex = null) {
      const month = monthIndex !== null ? monthIndex : this.currentMonth;
      const daysInMonth = new Date(this.currentYear, month + 1, 0).getDate();
      return Array.from({
        length: daysInMonth
      }, (_, i) => i + 1);
    },
    getDaysInYear() {
      const months = [];
      for (let month = 0; month < 12; month++) {
        const daysInMonth = new Date(this.currentYear, month + 1, 0).getDate();
        const days = Array.from({
          length: daysInMonth
        }, (_, i) => i + 1);
        months.push({
          month: format(new Date(this.currentYear, month), "MMMM"),
          days
        });
      }
      return months;
    },
    isWeekend(day, month = this.currentMonth) {
      const date = new Date(this.currentYear, month, day);
      const dayOfWeek = date.getDay();
      return {
        isWeekend: dayOfWeek === 0 || dayOfWeek === 6,
        dayOfWeek
      };
    },
    isEventOnDay(event, day) {
      const eventStart = new Date(event.startDate);
      const eventEnd = new Date(event.endDate);
      const dayOfWeek = day.getDay();
      const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;
      const dayDate = new Date(day.getFullYear(), day.getMonth(), day.getDate());
      const eventStartDate = new Date(eventStart.getFullYear(), eventStart.getMonth(), eventStart.getDate());
      const eventEndDate = new Date(eventEnd.getFullYear(), eventEnd.getMonth(), eventEnd.getDate());
      if (isWeekend) {
        // Pour les week-ends, afficher uniquement les événements qui commencent un week-end
        return (eventStart.getDay() === 0 || eventStart.getDay() === 6) && dayDate >= eventStartDate && dayDate <= eventEndDate;
      } else {
        // Pour les jours de semaine, afficher uniquement les événements qui commencent un jour de semaine
        return eventStart.getDay() !== 0 && eventStart.getDay() !== 6 && dayDate >= eventStartDate && dayDate <= eventEndDate;
      }
    },
    getEventsForMonth(resourceName, monthIndex, day) {
      const date = new Date(this.currentYear, monthIndex, day);
      return this.events.filter(event => event.resource === resourceName && this.isEventOnDay(event, date));
    },
    async fetchResourcesAndEvents() {
      try {
        const response = await axios.get(`${API_URL}/interventions`, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`
          }
        });
        const data = response.data;
        this.resources = data.map(user => ({
          id: user.id,
          name: user.name,
          agence: user.agence,
          interventions: user.interventions
        }));
        this.events = data.flatMap(user => user.interventions.map(intervention => ({
          id: intervention.id,
          name: intervention.commentaire,
          startDate: intervention.date_debut,
          endDate: intervention.date_fin,
          resource: user.name,
          color: intervention.client_color,
          resourceId: user.id,
          clientAbbreviation: intervention.client_abbreviation,
          clientName: intervention.client_name
        })));

        // Filtrer les utilisateurs et les jours fériés en fonction de l'agence sélectionnée
        if (this.selectedPays) {
          this.resources = this.resources.filter(resource => resource.agence && resource.agence.pays === this.selectedPays);
          this.joursFeries = this.joursFeries.filter(jour => jour.agence && jour.agence.pays === this.selectedPays);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération des données:", error);
      }
    },
    getClientColor(color) {
      if (!color || typeof color !== "number") {
        return "bg-blue-400"; // Couleur par défaut si la couleur n'est pas définie ou invalide
      }
      switch (color) {
        case 1:
          return "bg-orange-400";
        // Client (Orange)
        case 2:
          return "bg-yellow-400";
        // Prévision (Jaune)
        case 3:
          return "bg-sky-400";
        // Voyage (Bleu clair)
        case 4:
          return "bg-blue-300";
        // Férié/Congé (Bleu ciel)
        case 5:
          return "bg-blue-500";
        // Formation (Bleu)
        case 6:
          return "bg-red-400";
        // Récupération (Rouge)
        case 7:
          return "bg-purple-600";
        // Congé maladie (Violet)
        case 8:
          return "bg-sky-600";
        // Université (Sky)
        case 9:
          return "bg-teal-300";
        // Avant Vente (Zinc)
        default:
          return "bg-blue-400";
      }
    }
  }
};
