import { getWithCache } from '../../api';
import axios from 'axios';

const state = () => ({
    users: [],
    agencies: [],
    showCompetenceModal: false,
    selectedUser: {},
    selectedAgency: null,
    currentPage: 1,
    itemsPerPage: 10,
});

const mutations = {
    setUsers(state, users) {
        state.users = users;
    },
    setAgencies(state, agencies) {
        state.agencies = agencies;
    },
    setShowCompetenceModal(state, show) {
        state.showCompetenceModal = show;
    },
    setSelectedUser(state, user) {
        state.selectedUser = user;
    },
    setSelectedAgency(state, agency) {
        state.selectedAgency = agency;
    },
    setCurrentPage(state, page) {
        state.currentPage = page;
    },
};

const actions = {
    async fetchUsers({ commit, state }) {
        try {
            let url = "http://127.0.0.1:8000/api/users/type-zero";
            if (state.selectedAgency) {
                url += `?agence_id=${state.selectedAgency}`;
            }
            const response = await getWithCache(url, {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });
            commit('setUsers', response.data);
        } catch (error) {
            console.error("Error fetching users:", error);
        }
    },
    async fetchAgencies({ commit }) {
        try {
            const response = await getWithCache("http://127.0.0.1:8000/api/agences", {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });
            commit('setAgencies', response.data.agences);
        } catch (error) {
            console.error("Error fetching agencies:", error);
        }
    },
    async toggleUserStatus({ commit, state }, user) {
        try {
            const endpoint = user.active
                ? `http://127.0.0.1:8000/api/users/${user.id}/deactivate`
                : `http://127.0.0.1:8000/api/users/${user.id}/activate`;

            await axios.post(endpoint, null, {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });

            commit('setUsers', state.users.map(u => u.id === user.id ? { ...u, active: !u.active } : u));
        } catch (error) {
            console.error("Error toggling user status:", error);
        }
    }

};

export default {
    namespaced: true,
    state,
    mutations,
    actions,
};
