        filteredEvents[resource.name] = {};
        const days = this.viewMode === "month" ? this.getDaysInMonth() : this.getDaysInYear().flatMap(month => month.days);
        days.forEach(day => {
          const dayDate = new Date(this.currentYear, this.viewMode === "month" ? this.currentMonth : 0, typeof day === "number" ? day : day.day);
          filteredEvents[resource.name][day] = this.events.filter(event => event.resource === resource.name && this.isEventOnDay(event, dayDate));
        });
      });
      return filteredEvents;
