  methods: {
    ...mapActions('myCalendar', ['fetchUserData', 'fetchResourcesAndEvents', 'fetchClients', 'fetchJoursFeries']),
    ...mapMutations('myCalendar', ['SET_SEARCH_START_DATE', 'SET_SEARCH_END_DATE', 'SET_CURRENT_YEAR', 'SET_CURRENT_MONTH', 'SET_CURRENT_DAY']),
    getDayAbbreviation(date) {
      const days = ['dim', 'lun', 'mar', 'mer', 'jeu', 'ven', 'sam'];
      return days[date.getDay()];
    },
    isWednesday(day, monthIndex) {
