    },
    isJourFerie(day, monthIndex = null) {
      const date = new Date(this.currentYear, monthIndex !== null ? monthIndex : this.currentMonth, typeof day === 'number' ? day : new Date(day).getDate());
      const formattedDate = format(date, 'yyyy-MM-dd');
      return this.formattedJoursFeries.some(jour => jour.date === formattedDate);
    },
    calculateBusinessDays(startDate, endDate) {
      let count = 0;
      const currentDate = new Date(startDate);
      while (currentDate <= endDate) {
        const dayOfWeek = currentDate.getDay();
        if (dayOfWeek !== 0 && dayOfWeek !== 6) {
          count++;
        }
        currentDate.setDate(currentDate.getDate() + 1);
      }
      return count;
