      const resource = this.resources.find(r => r.name === resourceName);
      if (!resource) return false;
      const date = new Date(this.currentYear, monthIndex !== undefined ? monthIndex : this.currentMonth, day);
      const startDate = new Date(this.searchStartDate);
      const endDate = new Date(this.searchEndDate);
      if (date < startDate || date > endDate || this.isJourFerie(day)) return false;
      const tnfDays = this.getTNFDaysInPeriod(resource, startDate, endDate);
      return tnfDays.some(tnfDay => tnfDay.getDate() === date.getDate() && tnfDay.getMonth() === date.getMonth() && tnfDay.getFullYear() === date.getFullYear());
    },
    updateSelectedClient() {
      const selectedClient = this.clients.find(client => client.id === this.selectedClientId);
      if (selectedClient) {
        this.selectedClientColor = selectedClient.color;
