import axios from 'axios';
import { format } from 'date-fns';

const state = () => ({
    currentYear: new Date().getFullYear(),
    currentMonth: new Date().getMonth(),
    currentDay: new Date().getDate(),
    viewMode: 'month',
    resources: [],
    events: [],
    clients: [],
    isEventModalOpen: false,
    selectedEvent: {},
    selectedClient: null,
    selectedClientId: null,
    comment: '',
    startDate: '',
    endDate: '',
    importance: 1,
    user_id: null,
    monthsOfYear: [
        'Janvier',
        'Février',
        'Mars',
        'Avril',
        'Mai',
        'Juin',
        'Juillet',
        'Août',
        'Septembre',
        'Octobre',
        'Novembre',
        'Décembre',
    ],
    clientSearch: '',
    joursFeries: [],
    bangeResults: [],
    isBANGEModalOpen: false,
    searchStartDate: '',
    searchEndDate: '',
    tnfSearchResults: [],
    hoveredEvent: null,
    hoveredDay: null,
    bangeStartDate: '',
    bangeEndDate: '',
    selectedClientColor: '',
});

const mutations = {
    setCurrentYear(state, year) {
        state.currentYear = year;
    },
    setCurrentMonth(state, month) {
        state.currentMonth = month;
    },
    setCurrentDay(state, day) {
        state.currentDay = day;
    },
    setViewMode(state, mode) {
        state.viewMode = mode;
    },
    setResources(state, resources) {
        state.resources = resources;
    },
    setEvents(state, events) {
        state.events = events;
    },
    setClients(state, clients) {
        state.clients = clients;
    },
    setIsEventModalOpen(state, isOpen) {
        state.isEventModalOpen = isOpen;
    },
    setSelectedEvent(state, event) {
        state.selectedEvent = event;
    },
    setSelectedClient(state, client) {
        state.selectedClient = client;
    },
    setSelectedClientId(state, clientId) {
        state.selectedClientId = clientId;
    },
    setComment(state, comment) {
        state.comment = comment;
    },
    setStartDate(state, startDate) {
        state.startDate = startDate;
    },
    setEndDate(state, endDate) {
        state.endDate = endDate;
    },
    setImportance(state, importance) {
        state.importance = importance;
    },
    setUserId(state, userId) {
        state.user_id = userId;
    },
    setJoursFeries(state, joursFeries) {
        state.joursFeries = joursFeries;
    },
    setBangeResults(state, bangeResults) {
        state.bangeResults = bangeResults;
    },
    setSearchStartDate(state, searchStartDate) {
        state.searchStartDate = searchStartDate;
    },
    setSearchEndDate(state, searchEndDate) {
        state.searchEndDate = searchEndDate;
    },
    setTnfSearchResults(state, tnfSearchResults) {
        state.tnfSearchResults = tnfSearchResults;
    },
    setHoveredEvent(state, hoveredEvent) {
        state.hoveredEvent = hoveredEvent;
    },
    setHoveredDay(state, hoveredDay) {
        state.hoveredDay = hoveredDay;
    },
    setBangeStartDate(state, bangeStartDate) {
        state.bangeStartDate = bangeStartDate;
    },
    setBangeEndDate(state, bangeEndDate) {
        state.bangeEndDate = bangeEndDate;
    },
    setSelectedClientColor(state, selectedClientColor) {
        state.selectedClientColor = selectedClientColor;
    },
};

const actions = {
    async fetchUserData({  }) {
        try {
            const response = await axios.get('http://127.0.0.1:8000/api/me', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            return response.data;
        } catch (error) {
            console.error('Error fetching user data:', error);
            return null;
        }
    },
    async fetchResourcesAndEvents({ commit }, userId) {
        try {
            const response = await axios.get(`http://127.0.0.1:8000/api/mycalendar`, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            const data = response.data;
            commit('setResources', [
                {
                    id: data.id,
                    name: data.name,
                    interventions: data.interventions,
                },
            ]);
            commit('setEvents', data.interventions.map((intervention) => ({
                id: intervention.id,
                name: intervention.commentaire,
                startDate: intervention.date_debut,
                endDate: intervention.date_fin,
                resource: data.name,
                color: intervention.client_color,
                resourceId: data.id,
                clientAbbreviation: intervention.client_abbreviation,
            })));
        } catch (error) {
            console.error('Error fetching data:', error.response ? error.response.data : error.message);
        }
    },
    async fetchClients({ commit }) {
        try {
            const response = await axios.get('http://127.0.0.1:8000/api/clients', {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            commit('setClients', response.data);
        } catch (error) {
            console.error('Error fetching clients:', error);
        }
    },
    async fetchJoursFeries({ commit }) {
        try {
            const response = await axios.get('http://127.0.0.1:8000/api/jours-feries', {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            commit('setJoursFeries', response.data);
        } catch (error) {
            console.error('Error fetching public holidays:', error);
        }
    },
    async deleteEvent({ commit, state }) {
        try {
            await axios.delete(`http://127.0.0.1:8000/api/interventions/${state.selectedEvent.id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            await dispatch('fetchResourcesAndEvents', state.user_id);
            commit('setIsEventModalOpen', false);
        } catch (error) {
            console.error('Error deleting event:', error);
            if (error.response) {
                console.error('Error details:', error.response.data);
            }
        }
    },
};

const getters = {
    filteredClients: (state) => {
        return state.clients.filter((client) =>
            client.nom.toLowerCase().includes(state.clientSearch.toLowerCase())
        );
    },
    displayPeriod: (state) => {
        if (state.viewMode === 'month') {
            const firstDayOfMonth = new Date(state.currentYear, state.currentMonth, 1);
            return format(firstDayOfMonth, 'MMMM yyyy');
        } else if (state.viewMode === 'year') {
            const firstDayOfYear = new Date(state.currentYear, 0, 1);
            return format(firstDayOfYear, 'yyyy');
        } else {
            return `${state.currentYear}`;
        }
    },
    formattedJoursFeries: (state) => {
        return state.joursFeries.map((jourFerie) => ({
            date: format(new Date(jourFerie.date_ferie), 'yyyy-MM-dd'),
            name: jourFerie.nom_ferie,
        }));
    },
    periodDays: (state) => {
        return state.viewMode === 'month' ? getDaysInMonth(state.currentYear, state.currentMonth) : getDaysInYear(state.currentYear);
    },
    filteredResources: (state) => {
        if (!state.searchStartDate || !state.searchEndDate) {
            return state.resources;
        }
        const startDate = new Date(state.searchStartDate);
        const endDate = new Date(state.searchEndDate);
        return state.resources.filter((resource) => {
            const tnfDays = getTNFDaysInPeriod(resource, startDate, endDate);
            return tnfDays.length > 0;
        });
    },
    filteredEventsByDay: (state) => {
        const filteredEvents = {};
        state.resources.forEach((resource) => {
            filteredEvents[resource.name] = {};
            state.periodDays.forEach((day) => {
                const dayDate = new Date(state.currentYear, state.currentMonth, day);
                filteredEvents[resource.name][day] = state.events.filter(
                    (event) =>
                        event.resource === resource.name && isEventOnDay(event, dayDate)
                );
            });
        });
        return filteredEvents;
    },
};

export default {
    namespaced: true,
    state,
    mutations,
    actions,
    getters,
};
