import { getWithCache } from '../../api';

const state = {
    agences: [],
    searchName: "",
    searchPays: "",
    showAddModal: false,
    showEditModal: false,
    currentAgence: { id: null, name: "", pays: "" },
    currentPage: 1,
    itemsPerPage: 10,
};

const getters = {
    filteredAgences: (state) => {
        if (!Array.isArray(state.agences)) {
            return [];
        }
        return state.agences.filter((agence) =>
            agence.name.toLowerCase().includes(state.searchName.toLowerCase()) &&
            agence.pays.toLowerCase().includes(state.searchPays.toLowerCase())
        );
    },
    uniquePays: (state) => {
        const pays = state.agences.map(agence => agence.pays);
        return [...new Set(pays)];
    },
    totalAgencesPerPays: (state) => (pays) => {
        return state.agences.filter(agence => agence.nae === pays).length;
    },
    totalPages: (state, getters) => {
        return Math.ceil(getters.filteredAgences.length / state.itemsPerPage);
    },
};

const mutations = {
    SET_AGENCES(state, agences) {
        state.agences = agences;
    },
    SET_SEARCH_NAME(state, query) {
        state.searchName = query;
    },
    SET_SEARCH_PAYS(state, query) {
        state.searchPays = query;
    },
    SET_SHOW_ADD_MODAL(state, value) {
        state.showAddModal = value;
    },
    SET_SHOW_EDIT_MODAL(state, value) {
        state.showEditModal = value;
    },
    SET_CURRENT_AGENCE(state, agence) {
        state.currentAgence = agence;
    },
    SET_CURRENT_PAGE(state, page) {
        state.currentPage = page;
    },
    ADD_AGENCE(state, agence) {
        state.agences.push(agence);
    },
    UPDATE_AGENCE(state, agence) {
        const index = state.agences.findIndex(a => a.id === agence.id);
        if (index !== -1) {
            state.agences.splice(index, 1, agence);
        }
    },
    DELETE_AGENCE(state, id) {
        state.agences = state.agences.filter(agence => agence.id !== id);
    },
};

const actions = {
    async fetchAgences({ commit }) {
        try {
            const response = await getWithCache('/agences', {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });
            commit('SET_AGENCES', response.data.agences);
        } catch (error) {
            console.error("Error fetching agences:", error);
        }
    },
    async addAgence({ commit, dispatch }, agence) {
        try {
            const response = await getWithCache("/agences", {
                method: 'POST',
                data: agence,
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });
            const newAgence = response.data; // Assurez-vous que la réponse contient les données de la nouvelle agence

            commit('ADD_AGENCE', newAgence);
            commit('SET_SHOW_ADD_MODAL', false);
            commit('SET_CURRENT_AGENCE', { id: null, name: "", pays: "" });

            // Récupérer et afficher la liste des agences après l'ajout
            await dispatch('fetchAgences');
        } catch (error) {
            console.error(error);
        }
    },
    async updateAgence({ commit, dispatch }, agence) {
        try {
            const response = await getWithCache(`/agences/${agence.id}`, {
                method: 'PUT',
                data: agence,
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
            });
            const updatedAgence = response.data; // Assurez-vous que la réponse contient les données de l'agence mise à jour
            commit('UPDATE_AGENCE', updatedAgence);
            commit('SET_SHOW_EDIT_MODAL', false);
            commit('SET_CURRENT_AGENCE', { id: null, name: "", pays: "" });

            // Récupérer et afficher la liste des agences après la mise à jour
            await dispatch('fetchAgences');
        } catch (error) {
            console.error(error);
        }
    },
    async deleteAgence({ commit, dispatch }, id) {
        if (confirm("Êtes-vous sûr de vouloir supprimer cette agence ?")) {
            try {
                await getWithCache(`/agences/${id}`, {
                    method: 'DELETE',
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${localStorage.getItem("token")}`,
                    },
                });
                commit('DELETE_AGENCE', id);

                // Récupérer et afficher la liste des agences après la suppression
                await dispatch('fetchAgences');
            } catch (error) {
                console.error(error);
            }
        }
    },
};

export default {
    namespaced: true,
    state,
    getters,
    mutations,
    actions,
};
