        // Récupération (Rouge)
        case 7:
          return "bg-purple-600";
        // Congé maladie (Violet)
        case 8:
          return "bg-sky-600";
        // Université (Sky)
        default:
          return "bg-blue-400";
      }
    }
  }
};
